export class IndentedTextWriter {
    _textHolder = '';
    indent = 4;
    level = 0;
    get position() {
        return this._textHolder.length;
    }
    isLastCharNewline() {
        return this._textHolder[this._textHolder.length - 1] === '\n';
    }
    levelRaise() {
        this.level++;
    }
    levelShrink() {
        this.level--;
    }
    write(text) {
        this._textHolder += text;
    }
    writeLine(text) {
        this.writeIndent();
        this._textHolder += text;
        this.writeNewline();
    }
    writeIndent() {
        this._textHolder += ''.padEnd(this.level * this.indent, ' ');
    }
    writeNewline() {
        this._textHolder += '\n';
    }
    getString() {
        return this._textHolder;
    }
}
