export class IndentedTextWriter {
    constructor() {
        this._textHolder = '';
        this.indent = 4;
        this.level = 0;
    }
    get position() {
        return this._textHolder.length;
    }
    levelRaise() {
        this.level++;
    }
    levelShrink() {
        this.level--;
    }
    write(text) {
        this._textHolder += text;
    }
    writeLine(text) {
        this.writeIndent();
        this._textHolder += text;
        this.writeNewline();
    }
    writeIndent() {
        this._textHolder += ''.padEnd(this.level * this.indent, ' ');
    }
    writeNewline() {
        this._textHolder += '\n';
    }
    getString() {
        return this._textHolder;
    }
}
