export class PropertiesHelper {
    static isTypescriptEnum(value) {
        if (value && typeof value === 'object' && value.constructor == Object) {
            for (let k in value) {
                const tp = typeof value[k];
                if (tp !== 'string' && tp !== 'number')
                    return false;
            }
            return true;
        }
        return false;
    }
    static getTypescriptEnumEntries(value) {
        let ret = [];
        for (let k in value) {
            if (isNaN(k))
                ret.push([k, value[k]]);
        }
        return ret;
    }
    static camelToDashCase(text) {
        return text.replace(/([A-Z])/g, (g) => `-${g[0].toLowerCase()}`);
    }
    static dashToCamelCase(text) {
        return text.replace(/-([a-z])/g, (i) => i[1].toUpperCase());
    }
}
