import { PositionExtension } from "./PositionExtension.js";
export class PositionExtensionProvider {
  shouldExtend(designItem) {
    const cs = getComputedStyle(designItem.element);
    if (cs.position === 'relative' || cs.position === 'absolute') return true;
    return false;
  }

  getExtension(designerView, designItem) {
    return new PositionExtension(designerView, designItem);
  }

}