export class MultipleItemsSelectedContextMenu {
  constructor() {
    this.orderIndex = 60;
  }

  shouldProvideContextmenu(designerView, designItem) {
    if (designItem.instanceServiceContainer.selectionService.selectedElements.length > 1) {
      return true;
    }

    return false;
  }

  provideContextMenuItems(designerView, designItem) {
    return [{
      title: 'wrap in',
      children: [{
        title: 'div',
        action: () => {}
      }]
    }];
  }

}